INSERT INTO `tb_goods` VALUES (2, 'Venum Impact 拳击手套', 620, 0.80, 99, 'https://www.venum.com.cn/media/catalog/product/cache/3d6373dfdbd8bf7042581a31874a0831/7/4/74ce4135378d82a0f66953e3801d9565cd92fa18_BG_IMPACT_BLACK_GOLD_1500_01__1_.jpg', '\r\n\r\nVenum Impact 拳击手套 ：把对手全部击倒。\r\n\r\n这款 Venum Impact 拳击手套 可跟他们来真的：这件真正的大规模杀伤性武器将帮助您粉碎所有对手。\r\n\r\n特有的三重密度泡沫特别设计以完全附着的拇指来保护双手以避免受伤。\r\n\r\n有备无患：长袖口可以改善手腕的保护，而掌心网状面板则可保持手部凉爽。\r\n\r\n坚持不懈地战斗，全力以赴。 让每个对手都倒在地板上。', 2, 'https://www.venum.com.cn/media/catalog/product/cache/91981264edb3dd07a6a748b8e0dac630/1/8/18452cdb3cc5db326677652c8460418f1e9c1277_BG_IMPACT_BLACK_GOLD_1500_03__1_.jpg,,,https://www.venum.com.cn/media/catalog/product/cache/3d6373dfdbd8bf7042581a31874a0831/6/a/6aa644984de0db06f2d023396b625a57859e0922_BG_IMPACT_BLACK_GOLD_1500_04__1_.jpg', 0, '8 OZ,10 OZ,12 0Z,14 OZ,16 OZ', 1);
